        },
        showMore() {
            this.itemsLimit += PAGE_SIZE;
        },
        resetLimit() {
            this.itemsLimit = PAGE_SIZE;
        },
        formatServer(server) {
            return `*.${server.split('.').slice(-2).join(".")}`
        }
    },
    computed: {
        maliciousPackagesCount() {
            return this.items.length
        },
        userAccountsCount() {
            let users = new Set();
            this.items.forEach(item => users.add(item.username));
            return users.size;
        },
        exfiltrationEndpointsCount() {
            let servers = new Set();
            this.items.forEach(item => servers.add(item.server));
            return servers.size;
        },

        filteredItems() {
            let items = this.items;
            let search = this.search;

            if (search) {
                items = items.filter(x => x.name.includes(search))
            }

            return items;
        }
    }
}
</script>

